import React, {useState, FC} from 'react'
import { useAppSelector, useAppDispatch } from '../../app/hooks';
import { AppDispatch } from '../../app/store';
import { saveVpcName } from './vpcSlice';

export const VpcNameForm: FC = (props) => {

  const vpcName = useAppSelector((state) => state.vpcName.value)
  const dispatch: AppDispatch = useAppDispatch()
  const [customVpcName, setCustomVpcName] = useState('exampleVpc')

  return (
    <form>
      <label>
        VPC NAME:
      </label>
      <input
        type="text"
        placeholder='exampleVPC'
        name="customVpcName"
        value={customVpcName}
        onChange={(e) => setCustomVpcName(e.target.value)}
      />
      <button onClick={() => dispatch(saveVpcName(customVpcName))}></button>
    </form>
  );
  }
