import React, {useState, FC} from 'react'
import { useAppSelector, useAppDispatch } from '../../app/hooks';
import { AppDispatch, store } from '../../app/store';
import { saveVpcName } from './vpcSlice';

export const VpcNameForm: FC = (props) => {

  const vpcName = useAppSelector((state) => state.vpcName.value)
  const dispatch: AppDispatch = useAppDispatch()
  const [yourVpcName, setYourVpcName] = useState('exampleVpc')
  
  const setVpcName = useAppSelector((state) => state.vpcName.value)

  const handleChange = (e:any) => {
    dispatch(saveVpcName(yourVpcName))
    console.log(setVpcName)
    e.preventDefault()
  }

  
  // async function setYourVpcNameToStore() {
  //   return new Promise(resolve => {
  //     dispatch(saveVpcName(yourVpcName))
  //     resolve("set your vpc name to store")
  //   })
  // }


  // async function consoleLogStore() {
  //   return new Promise(resolve => {
  //     resolve("show vpc name")
  //     console.log(setVpcName)
  //   })
  // }

  // async function handleSubmit(e: any) {
  //   await setYourVpcNameToStore()
  //   await consoleLogStore()
  //   e.preventDefault()
  // }

  function setItemToLocalStorage(item: string) {
    localStorage.setItem(item)
  }

  return (
    <form>
      <label>
        VPC NAME:
      </label>
      <input
        type="text"
        placeholder='exampleVPC'
        name="yourVpcName"
        onChange={(e) => setYourVpcName(e.target.value)}
      />
      <button
        onClick={setItemToLocalStorage}>
        設定
        </button>
    </form>
  );
  }
